    def converter(self):
        x = self.lineEdit.text()
        y = self.comboBox.currentText()
        z = self.comboBox_2.currentText()
        if (x.isdecimal()):
            import conversion
            n = str(conversion.converter(y,z,float(x)))
            self.lineEdit_2.setText(x+" "+y+" is equal to "+n+" "+z)
        else:
            self.lineEdit_2.setText('Amount should be numeric')


